mapper.select:
		select f.*

                ,dc.collection_id

		FROM diary_filter f


                left outer join collection_items dc
                on f.id = dc.diary_id

            where member_id = #{memberId} 

				  and collection_id = #{collection}   

				and feeling = #{feeling} 

				and weather = #{weather}

				and Date(reg_date) = #{localDate}

                AND (title LIKE CONCAT('%', #{query}, '%')
                OR content LIKE CONCAT('%', #{query}, '%'))


        ORDER BY


                reg_date ASC


                reg_date DESC
